mapper.update:
update commission
set status='1',
    accept_name=#{acceptName},
    accept_student_id=#{acceptStudentId}
where id = #{commissionId}

mapper.select:
SELECT c.content, c.id, c.accept_student_id as acceptStudentId, c.student_id as studentId,
c.date, c.money, c.status, c.contact, c.address as commissionAddress,
c.accept_student_id as acceptStudentId, c.limit_sex as limitSex, s.id as studentId,
s.address as studentAddress, s.birthday, s.email, s.enrollment_time, s.`name`, s.school, s.avatar, s.phone FROM commission c LEFT JOIN student s ON c.student_id = s.id
where status=#{status}
order by ${orderField} ${orderType}
